import React from "react";
import { Routes, Route, Link, useNavigate } from "react-router-dom";
import Register from "./components/Register";
import Login from "./components/Login";
import PaymentForm from "./components/PaymentForm";
import PaymentsList from "./components/PaymentsList";
import { isLoggedIn, clearToken, loadToken } from "./auth";

function Home() {
  return (
    <div className="card">
      <h3>Welcome</h3>
      <p className="small">Use the Register page to create a customer account. Then login and create payments.</p>
    </div>
  );
}

export default function App() {
  const navigate = useNavigate();
  const logged = isLoggedIn();

  const logout = () => {
    clearToken();
    navigate("/");
    window.location.reload();
  };

  return (
    <div className="container">
      <div className="header">
        <h1>INSY7314 — Customer Portal</h1>
        <div className="nav">
          <Link to="/"><button>Home</button></Link>
          {!logged && <Link to="/register"><button>Register</button></Link>}
          {!logged && <Link to="/login"><button>Login</button></Link>}
          {logged && <Link to="/payments"><button>Payments</button></Link>}
          {logged && <button onClick={logout}>Logout</button>}
        </div>
      </div>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route path="/payments" element={
          <div>
            <PaymentForm onCreate={() => { /* PaymentsList will auto refresh on mount only */ }} />
            <PaymentsList />
          </div>
        } />
      </Routes>

      <footer style={{ marginTop: 18, fontSize: 12 }} className="small">
        Running in secure mode — uses HTTPS to communicate with backend. If using self-signed certs, accept the browser warning first.
      </footer>
    </div>
  );
}
